-- Variables
DECLARE @locationsCount INT;
DECLARE @measurementsCount INT;
DECLARE @devicesCount INT;

DECLARE @locationsCounter INT;
DECLARE @measurementsCounter INT;
DECLARE @devicesCounter INT;

--  We need minimal ID to fill data.
DECLARE @minLocationsID INT;
DECLARE @minDevicesID INT;
DECLARE @minMeasurementID INT;

SELECT @measurementsCount = COUNT(*) FROM Measurements;
SELECT @locationsCount = COUNT(*) FROM Locations;
SELECT @devicesCount = COUNT(*) FROM Devices;

--  Counter should start with smalles ID from table
SELECT @minLocationsID = MIN(ID) FROM Locations;
SELECT @minDevicesID = MIN(ID) FROM Devices;
SELECT @minMeasurementID = MIN(ID) FROM Measurements;

--  We have to add min ID value to count becuase counter is min ID based!!!!
SET @locationsCount = @locationsCount + @minLocationsID;
SET @devicesCount = @devicesCount + @minDevicesID;
SET @measurementsCount = @measurementsCount + @minMeasurementID;

SET @locationsCounter = @minLocationsID;
SET @devicesCounter = @minDevicesID;
SET @measurementsCounter = @minMeasurementID;

-- Outer loop: iterate over locations
WHILE @locationsCounter < @locationsCount
BEGIN
    -- Reset measurement counter for each location
    SET @measurementsCounter = @minMeasurementID;

    -- Inner loop: iterate over measurements
    WHILE @measurementsCounter < @measurementsCount
    BEGIN
        DECLARE @NewChartTemplateID INT;

        -- Insert a new chart template
        INSERT INTO ChartTemplates (MeasurementID) 
        VALUES (@measurementsCounter)
        
        SET @NewChartTemplateID = SCOPE_IDENTITY();

        -- Insert ChartOffset for this chart
        INSERT INTO ChartOffsets (Time, Value, LocationID, ChartTemplateID)
        VALUES (0, 0, @locationsCounter, @NewChartTemplateID);

        SET @measurementsCounter = @measurementsCounter + 1;
    END

    SET @locationsCounter = @locationsCounter + 1;
END

